%% Crazyflie PID Controllers%% On Board Controllerfs_inner=50;  % 500 Hzfs_outer=25;  % 250 Hz% CONTROLER MIXER: MatrixMCO =[1,-1/2,-1/2,-1;      1, 1/2,-1/2, 1;      1, 1/2, 1/2,-1;      1,-1/2, 1/2, 1];% RATE CONTROLLER% PID DphiKPp=70;KIp=0;KDp=0;% PID DthetaKPq=70;KIq=0;KDq=0;% PID DpsiKPr=70;KIr=16.7;KDr=0;% KPr=70;KIr=16.7;KDr=2.5;% ATTITUDE CONTROLLER% PID phiKPphi=3.5;KIphi=2.0;KDphi=0.0;phi_theta_limit = 100*pi;% PID thetaKPtheta=3.5;KItheta=2.0;KDtheta=0.0;%% Off Board Controller% YAW CONTROLLERKPyaw=3.0;KIyaw=0;KDyaw=0.0;% ALTITUDE CONTROLLERKPz=15000;KIz=3500;KDz=9000;% X-Y POSITION CONTROLLER% X -> phiKPxv=30;KIxv=2;KDxv=0;KPxp=1;KIxp=0;KDxp=0;% Y -> thetaKPy=-30;KIy=-2;KDy=0;% Limit control signalxyv_lim = 30; % [rad/s]