%% Crazyflie PID(s) Controllers% X_s = [KP; KI; KD] -> PID(s) controller%% On Board Controller% CONTROLER MIXER: MatrixMCO =[1,-1/2,-1/2,-1;      1, 1/2,-1/2, 1;      1, 1/2, 1/2,-1;      1,-1/2, 1/2, 1];%% RATE CONTROLLER% PID DphiDphi_s = [70; 0; 0];% PID DthetaDtheta_s = [70; 0; 0];% PID DpsiDpsi_s = [70; 16.7; 0];% Dpsi_s = [70; 16.7; 2.5];%% ATTITUDE CONTROLLERphi_theta_limit = 100*pi;% PID phiPhi_s = [3.5; 2.0; 0.0];% PID thetaTheta_s = [3.5; 2.0; 0.0];%% Off Board Controller% YAW CONTROLLERYaw_s = [3.0; 0.0; 0.0];% ALTITUDE CONTROLLERZ_s = [15000; 3500; 9000];W_s = [1.0; 0.0; 0.0];% X-Y POSITION CONTROLLER% Limit control signalxyv_lim = 30; % [rad/s]% X -> phiX_s = [1.0; 0.0; 0.0];U_s = [30; 2; 0];% Y -> thetaY_s = [1.0; 0.0; 0.0];V_s = [-30; -2; 0];